import 'package:flutter/material.dart';

class MyDrawer extends StatelessWidget {
  const MyDrawer({super.key});

  @override
  Widget build(BuildContext context) {
    return Drawer(
      width: MediaQuery.of(context).size.width * 0.5,
      child: Column(
        children: <Widget>[
          CircleAvatar(radius: 70, backgroundImage: NetworkImage('data:image/jpeg;base64,'),),
        
          Divider(
            color: Colors.grey,
            height: 20,         
            thickness: 1,       
            indent: 16,         
            endIndent: 16,  
          )
           ,ListTile(
            iconColor: Colors.blue,
            leading: Icon(Icons.thirty_fps_outlined),
            title: Text("Coisa 1"),)  
            ,
            ListTile(
              iconColor: Colors.blue,
              leading: Icon(Icons.thirty_fps),
              title: Text("Coisa 2"),)
              ]
              )
    );
  }
}