import 'package:aula6/model/profile.dart';
import 'package:aula6/widgets/mycard.dart';
import 'package:aula6/widgets/mydrawer.dart';
import 'package:aula6/widgets/mystory.dart';
import 'package:flutter/material.dart';

class MyHomePage extends StatelessWidget {
   
   List<Profile> listaDados = [
    Profile(name: "ronaldinho", image: "data:image/jpeg;base64,", like: false),
    Profile(name: "caio", image: "", like: true),
    Profile(name: "pero", image: "", like: false),
    Profile(name: "licolas", image: "", like: true)
  ]; 

 MyHomePage ({super.key});

  @override
  Widget build(BuildContext context) {
    return  Scaffold(
      appBar: AppBar(
        title: Text("aula 6!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!"),
        backgroundColor: Colors.amber,
      ),
      drawer: Mydrawer(),
      body: SingleChildScrollView(
        child: Column(
          children: <Widget>[
            SizedBox(
              height: 200,
              child: ListView.builder(
                scrollDirection: Axis.horizontal,
                itemCount: listaDados.length,
                itemBuilder: (context, itens) {
                  return Mycard(title: listaDados[itens].name, image: listaDados[itens].image,
                  like: listaDados[itens].like);
                  }
                  ),
            ),
             SizedBox(
              height: 200,
              child: ListView.builder(
                scrollDirection: Axis.horizontal,
                itemCount: listaDados.length,
                itemBuilder: (context, itens) {
                  return Mystory(title: listaDados[itens].name,
                  image: listaDados[itens].image,
                );
                  }
                  ),
            )
          ],
        ),
      ),
    );
  }
}